INSERT INTO `asso` (`id`, `name`, `login`, `pole_id`, `type_id`, `url_site`, `description`, `logo`, `active`, `passation`, `salle`, `phone`, `facebook`, `created_at`, `updated_at`) VALUES
(2, 'SiMDE', 'simde', 1, 1, 'http://wwwassos.utc.fr/simde', 'Service informatique de la Maison des Étudiants\r\nchargé de la mise en place de :\r\nRessource informatique au sein de la MDE\r\nLes serveurs : Phoenix et Centaure \r\nLe site wwwassos.utc.fr\r\nL''intranet des assos', 'http://wwwassos.utc.fr/simde.png', 1, NULL, NULL, NULL, NULL, '2011-05-14 11:06:17', '2011-05-14 11:06:24'),
(6, 'BDE-UTC', 'bde', 1, 3, 'http://wwwassos.utc.fr/', 'Le bureau des étudiants de l''UTC (BDE-UTC) est une association 1901 qui a pour objet l''animation et la structuration de la vie étudiante de l''UTC de manière saine, responsable et constructive.\r\n\r\nDans ce cadre le BDE-UTC administre la Maison des Étudiants (MDE), partie du bâtiment C du centre Benjamin Franklin, dans le cadre de la Convention d''Occupation de la MDE signée chaque année avec l''UTC. Depuis début septembre, le BDE et ses commissions s''attache à supprimer les nuisances sonores aux abords des bâtiments.', NULL, 1, NULL, NULL, NULL, NULL, '2011-05-14 11:32:40', '2011-05-14 11:32:42'),
(7, 'USEC', 'usec', 2, 3, 'http://wwwassos.utc.fr/usec', 'Envie de toucher du doigt le monde de l''Entreprise? \r\nRejoins l''équipe de l''USEC, Junior-Entreprise de l''UTC! \r\n\r\nProche du fonctionnement les entreprises de conseil, nous mettons à disposition de clients industriels, du secteur tertiaire et particuliers, les compétences et connaissances acquises durant notre cycle ingénieur à l''UTC. Et bien sûr, chaque étudiant qui travaille pour l''USEC est rémunéré!\r\n\r\nL''USEC sera ton lien privilégié avec les entreprises durant tes années à l''UTC!\r\n\r\nViens nous voir en FC221!!', NULL, 1, NULL, NULL, NULL, NULL, '2011-05-14 11:33:41', '2011-05-14 11:33:43'),
(8, 'Polar', 'polar', 1, 3, 'http://wwwassos.utc.fr/polar', 'LA centrale d''achat des étudiants : matériel informatique, fournitures, papier ; mais aussi tout le service : impressions de rapports, posters, location de vidéoprojecteur, de WiiPack, de manuel de langue, et bien d''autres encore !', 'http://wwwassos.utc.fr/polar/styles/0/textures/logo.png', 1, NULL, NULL, NULL, NULL, '2011-05-14 11:35:22', '2011-05-14 11:35:24'),
(9, 'Comet', 'comet', 3, 1, 'http://wwwassos.utc.fr/comet', 'Organisation des soirées Étudiantes (Estu), location de matériel de son et lumières à bas prix.', 'http://wwwassos.utc.fr/poleae/logos/comet.jpg', 1, NULL, NULL, NULL, NULL, '2011-05-14 11:37:01', '2011-05-14 11:37:03');
